import { Sequelize } from 'sequelize';
const sequelize = require('../index')

const User = sequelize.define(
  'user',
  {
    id: {
      allowNull: false,
      autoIncrement: true,
      field: 'id',
      primaryKey: true,
      type: Sequelize.BIGINT,
    },
    login: {
      allowNull: false,
      field: 'login',
      type: Sequelize.STRING,
      unique: true,
    },
    password: {
